INSERT INTO chatbot_conversations (session_id, user_message, bot_response, api_used, response_time_ms) VALUES
('session_001', 'Xin chào', 'Xin chào! Tôi là AI Assistant cho dự án YOLO nhận diện ngủ gật. Tôi có thể giúp bạn tìm hiểu về computer vision, machine learning, và các thuật toán YOLO.', 'local', 150),
('session_001', 'YOLO là gì?', 'YOLO (You Only Look Once) là thuật toán nhận dạng đối tượng thời gian thực được phát triển bởi Joseph Redmon vào năm 2016. Nó có thể phát hiện và phân loại nhiều đối tượng trong một hình ảnh chỉ với một lần nhìn, rất hiệu quả cho ứng dụng thời gian thực!', 'groq', 1200),
('session_001', 'Dự án này làm gì?', 'Đây là dự án nghiên cứu về ứng dụng YOLO trong phát hiện sinh viên ngủ gật trong lớp học. Mục tiêu là cải thiện chất lượng học tập và giúp giáo viên theo dõi tình trạng sinh viên.', 'local', 200),
('session_002', 'Machine learning là gì?', 'Machine Learning là lĩnh vực nghiên cứu các thuật toán và mô hình thống kê cho phép máy tính học từ dữ liệu mà không cần được lập trình rõ ràng. Trong dự án này, chúng ta sử dụng supervised learning để huấn luyện mô hình nhận diện trạng thái ngủ gật.', 'groq', 1100),
('session_002', 'Kết quả như thế nào?', 'Kết quả đạt được: mAP 94.2%, Precision 96.8%, Recall 92.1%, tốc độ xử lý 25 FPS. Đây là những chỉ số rất tốt cho một hệ thống nhận diện thời gian thực.', 'local', 180),
('session_003', 'CNN là gì?', 'CNN (Convolutional Neural Network) là kiến trúc mạng nơ-ron chuyên biệt cho xử lý dữ liệu có cấu trúc lưới như hình ảnh. CNN sử dụng các lớp tích chập để trích xuất đặc trưng từ hình ảnh một cách hiệu quả.', 'groq', 1300),
('session_003', 'Cảm ơn', 'Rất vui được giúp đỡ bạn! Hy vọng thông tin hữu ích cho việc nghiên cứu của bạn.', 'local', 120),
('session_004', 'Computer vision là gì?', 'Computer Vision là lĩnh vực nghiên cứu cách máy tính có thể hiểu và phân tích hình ảnh. Trong dự án này, chúng ta sử dụng CV để phát hiện các dấu hiệu ngủ gật như nhắm mắt, cúi đầu, hoặc giảm hoạt động của mắt.', 'groq', 1400),
('session_004', 'Liên hệ tác giả', 'Bạn có thể liên hệ với tác giả qua email: nhakhoa1004@gmail.com hoặc số điện thoại: 0395123864', 'local', 100),
('session_005', 'Deep learning là gì?', 'Deep Learning là một nhánh của Machine Learning sử dụng mạng nơ-ron nhân tạo với nhiều lớp để học các đặc trưng phức tạp từ dữ liệu. Ưu điểm của Deep Learning là tự động trích xuất đặc trưng, hiệu suất cao với dữ liệu lớn.', 'groq', 1500),
('session_005', 'YOLO có mấy phiên bản?', 'YOLO có nhiều phiên bản từ v1 (2016) đến v8 (2023). Mỗi phiên bản đều có cải tiến về độ chính xác và tốc độ. Dự án này đạt được mAP 94.2% và chạy với tốc độ 25 FPS!', 'local', 250),
('session_006', 'mAP là gì?', 'mAP (Mean Average Precision) là chỉ số đánh giá độ chính xác tổng thể của mô hình nhận diện đối tượng. Giá trị càng cao càng tốt. Trong dự án này đạt 94.2%.', 'groq', 1200),
('session_006', 'Precision và Recall là gì?', 'Precision (Độ chính xác) đo lường tỷ lệ dự đoán đúng trong tổng số dự đoán dương tính. Recall (Độ bao phủ) đo lường tỷ lệ dự đoán đúng trong tổng số trường hợp thực tế dương tính.', 'local', 300),
('session_007', 'Cách sử dụng demo?', 'Để sử dụng demo: 1. Click "Bắt Đầu Demo" để khởi động camera 2. Cho phép truy cập camera 3. Ngồi thẳng trước camera 4. Thử nghiệm bằng cách nhắm mắt hoặc cúi đầu 5. Quan sát kết quả phát hiện', 'groq', 1600),
('session_007', 'Cảm ơn bạn', 'Không có gì! Nếu có câu hỏi gì khác về YOLO hoặc computer vision, cứ hỏi nhé!', 'local', 150),
('session_008', 'Tác giả là ai?', 'Tôi là Nguyễn Hoàng Anh Khoa, sinh viên năm cuối ngành Công nghệ thông tin tại Đại học Đà Lạt. Đây là dự án nghiên cứu về YOLO của tôi.', 'local', 200),
('session_008', 'Trường nào?', 'Đại học Đà Lạt - một trường đại học uy tín tại Việt Nam, nổi tiếng với chất lượng đào tạo về công nghệ thông tin.', 'groq', 1000),
('session_009', 'Công nghệ sử dụng?', 'Dự án sử dụng: Frontend (HTML5, CSS3, JavaScript, WebRTC), Backend (Node.js, Express.js, MySQL), AI (YOLOv8, Groq API), Database (MySQL với connection pooling).', 'local', 400),
('session_009', 'Tính năng chính?', 'Tính năng chính: Real-time camera access, Live detection visualization, Interactive chatbot, Data export functionality, Database integration, API endpoints.', 'groq', 1300),
('session_010', 'Cảm ơn rất nhiều', 'Cảm ơn bạn đã quan tâm đến dự án! Chúc bạn học tập và nghiên cứu thành công.', 'local', 180);
